import React from 'react'
import { ReactComponent as SunIcon} from '../images/sun.svg';
import { ReactComponent as MoonIcon} from '../images/moon.svg';
import { motion } from "framer-motion";

export const Toggle = ({ theme, toggleTheme}) => {
    // console.log(theme);
  return (
    <motion.div onClick={toggleTheme} className="toggleBtn" whileHover={{scale: 1.2, originX: 0}} whileFocus={{opacity:0}}>{ theme === 'light' ? <SunIcon className='sunIcon'/> : <MoonIcon className='moonIcon'/>}</motion.div>
  )
}
