import React, { useState, useEffect } from "react";
import Cursor from "../components/Cursor";
import NavMain from "../components/NavMain";
import Hamburger from "../components/Hamburger";
import Logo from "../components/Logo";
import Particles from "react-tsparticles";

//import typewriter effect
import Typewriter from 'typewriter-effect';

function Home() {
  return (
    <main className="home-main">
      <Logo/>
      <Hamburger />
      <NavMain />
      <Particles
        canvasClassName="particle-canvas"
      options={{
        fullScreen: {
          enable: true
        },
        detectRetina: true,
        background: {
          color: "#000"
        },
        fpsLimit: 60,
        emitters: {
          direction: "top",
          life: {
            count: 0,
            duration: 0.08,
            delay: 0.1
          },
          rate: {
            delay: 0.15,
            quantity: 1
          },
          size: {
            width: 100,
            height: 0
          },
          position: {
            y: 100,
            x: 50
          }
        },
        particles: {
          number: {
            value: 0
          },
          life: {
            count: 5
          },
          shape: {
            type: "line"
          },
          size: {
            value: 50,
            animation: {
              enable: true,
              sync: true,
              speed: 1,
              startValue: "max",
              destroy: "min"
            }
          },
          stroke: {
            color: {
              value: "#2BFFE6"
            },
            width: 1
          },
          rotate: {
            path: true,
            value: 180
          },
          move: {
            straight: true,
            enable: true,
            speed: { min: 10, max: 20 },
            outModes: {
              default: "destroy",
              top: "none"
            },
            trail: {
              fillColor: "#000",
              enable: true,
              length: 3
            }
          }
        }
      }}
    />
      <div className="home-section">
          <h2>
            <div className="first-line">Hi,</div>
            <div className="second-line">I'm Ivan, </div> 
            <div className="third-line">
              <Typewriter 
                options= {{
                  autoStart: true,
                  loop: true,
                  delay: 40,
                  strings: [" Front-End Developerüíª", " UI/UX Designerüì≤", " Dog Loverüê∂"],
                }}
                />
              </div>
            </h2>
      </div>
      <a href="/portfolio/contact" className="cta-contact">Contact Me!</a>
      {/* <a href="/portfolio/about" class="mobile-bot-about">About</a> */}
    </main>
  );
}

export default Home;
