import React, { useState } from "react";
import { Link } from "react-router-dom";

function Hamburger() {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <div className="mobileNav">
      <button
        className="hamburgerBtn"
        onClick={() => {
          setIsOpen(!isOpen);
        }}
      >
        <div className={isOpen ? "hamburger animate" : "hamburger"}></div>
      </button>
      {isOpen && (
        <ul className="mobileNavLinks">
          <li>
            <Link to="/"
              onClick={() => {
                setIsOpen(!isOpen);
              }}
            >
              HOME
            </Link>
          </li>
          <li>
            <Link
              to="/about"
              onClick={() => {
                setIsOpen(!isOpen);
              }}
            >
              ABOUT
            </Link>
          </li>
          <li>
            <Link to="/work"
              onClick={() => {
                setIsOpen(!isOpen);
              }}
            >
              WORK
            </Link>
          </li>
          <li>
            <Link
              to="/contact"
              onClick={() => {
                setIsOpen(!isOpen);
              }}
            >
              CONTACT
            </Link>
          </li>
        </ul>
      )}
    </div>
  );
}

export default Hamburger;
