import React, { useEffect } from "react";
import TimoPic from "../images/timo-about.png";
import Hamburger from "../components/Hamburger";
import Logo from "../components/Logo";

//import libraries
import { motion } from "framer-motion";
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet'

function About() {

  const position = [51.505, -0.09];

  useEffect(() => {
    document.body.classList.remove("overflow-hidden");
  }, []);
  return (
    <main>
      <Logo/>
      <Hamburger />
        <a href="/portfolio" className='right-home'>Home</a>
        <a href="/portfolio/work" className='botright-work'>Work</a>
      <motion.div className="about" initial={{opacity:0}} animate={{opacity: 1}} transition={{duration: 1.5}}>

        <article>
            <h2 >About</h2>
            <div className="about-content">
                <img src={TimoPic} />
                <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. </p>
            </div>

            <section className="hobbies-section">
                <h2>Other things I enjoy...</h2>
                <ul>
                  <li>
                    <p>
                      <span className="hobby-title">Music🎵 </span>
                        I like to listen to Rap/HipHop Genre. In my free time, I will go through albums and try to pick out some good songs.</p>
                  </li>
                  <li>
                    <p>
                      <span className="hobby-title">Hockey </span>
                        I play ice and ball hockey depending on season. 4th line plug that can provide energy to the team. </p>
                  </li>
                  <li>
                    <p>
                      <span className="hobby-title">Video Games </span>
                        Previously a competitive Call of Duty Warzone player with a 2.50 KD. </p>
                  </li>
                  <li>
                    <p>
                      <span className="hobby-title">Adventures with Timo </span>
                        I love bringing Timo to beaches or hikes, and watch him have the time of his life! </p>
                  </li>
                </ul>

            </section>
            
        </article>



        {/* -------------------  corner box animations */}
        <motion.div className="cornerbox cornerbox1" animate={{scale:0.5, rotate:180}} transition={{yoyo: Infinity, duration: 2}} ></motion.div>
        <motion.div className="cornerbox cornerbox2" animate={{scale: 0.5, rotate:-180}} transition={{yoyo: Infinity, duration: 2}} ></motion.div>
        {/* ------------------------------------------ */}
      </motion.div>
      {/* <a href="/portfolio/work" className="mobile-bot-work">Work</a> */}
    </main>
  );
}

export default About;
