import React, { useState, useEffect } from "react";
import Cursor from "../components/Cursor";
import NavMain from "../components/NavMain";
import Hamburger from "../components/Hamburger";
import Logo from "../components/Logo";
import BrushBtn from "../images/brush-btn.png";
import { motion } from "framer-motion";

//import typewriter effect
import Typewriter from 'typewriter-effect';

function Home() {


  useEffect(() => {
    document.body.classList.add("overflow-hidden");
  }, []);

  return (
    <main className="home-main">
      <Logo/>
      <Hamburger />
      <NavMain />
      {/* star background Spans */}
      <div className="starBackground" >
        <span></span>
        <span></span>
        <span></span>
        <span></span>
        <span></span>
        <span></span>
        <span></span>
        {/* <span></span>
        <span></span>
        <span></span> */}
      </div>
      <div className="home-section">
          <h2>
            <div className="first-line">Hi,</div>
            <div className="second-line">I'm Ivan, </div> 
            <div className="third-line">
              <Typewriter 
                options= {{
                  autoStart: true,
                  loop: true,
                  delay: 40,
                  strings: [" Front-End Developer💻", " UI/UX Designer📲", " Dog Lover🐶"],
                }}
                />
              </div>
            </h2>
      </div>
      <motion.a href="/portfolio/contact" className="cta-contact" whileHover={{scale: 1}}>
          <span>Contact Me!</span>
          <motion.img src={BrushBtn} className="brush-button" initial={{opacity:0}} animate={{opacity:1}} transition={{ ease: "easeOut", duration: 2 }} ></motion.img>
      </motion.a>
      {/* <a href="/portfolio/about" class="mobile-bot-about">About</a> */}
    </main>
  );
}

export default Home;
